import React, { useState } from 'react';
import axios from 'axios';

function Home() {
  const [allUrls, setUrls] = useState([]);
  const RetrieveUrls = (e) => {
    e.preventDefault();
    axios.post('http://localhost:3001/api/featured')
    .then(response => {
      console.log("Response data: " + JSON.stringify(response.data));
      setUrls(response.data);
      console.log(allUrls);
    })
  }

    return (
        <div>
          <header>
            <h1>Welcome to PhotoShare</h1>
            <nav>
              <ul>
                <li><a href="/explore">Explore</a></li>
                <li><a href="#">Notifications</a></li>
                <li><a href="/profile">Profile</a></li>
                <li><a href="/leaderboard">Leaderboard</a></li>
              </ul>
            </nav>
          </header>
          <main>
            <h2>Featured Photos</h2>  
            <div className="featured-photos">
              <button onClick={RetrieveUrls} class='btn btn-success w-150 rounded-0'>View</button>
              <view style={{display:'flex',flexDirection: 'row', flexWrap:'wrap'}}>
              {allUrls.map(item => (
                <view style={{display:'flex',flexDirection: 'column', flexWrap:'nowrap'}}>
                  <li key={item.id}>
                  <img style = {{height:128, width:128, padding:10}} src={item.data}/>
                  <p>{item.caption}</p>
                  </li>
                </view>
             
            ))}
             </view>
              {/* Display featured photos here */}
            </div>
          </main>
          <footer>
            <p>&copy; 2023 PhotoShare. All rights reserved.</p>
          </footer>
        </div>
      );
}
export default Home
