import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import validation from './signupValidation';
import axios from 'axios'

let userCount = 0;

function Signup() {

    const [values, setValues] = useState({
        firstName: '',
        lastName: '',
        email: '',
        password: '',
        gender: '',
        hometown: '',
        dob: ''
    })

    const [errors, setErrors] = useState({})

    const handleInput = (event) => {
        setValues(prev =>({...prev, [event.target.name]: [event.target.value] }))
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(validation(values));
        if(errors.name === "" && errors.email === "" && errors.password === "" && errors.hometown === "") {
            axios.post('http://localhost:3000/api/signup', [userCount, firstName, lastName, email, password, gender, hometown, dob])
            .then(res => console.log(res))
            .catch(err => console.log(err));
        }
    }

    return (
        <div className='d-flex justify-content-center align-items-center bg-black vh-100'>
            <div className='bg-white p-3 rounded w-25'>
                <h2>Sign-Up</h2>
                <form action="" onSubmit={handleSubmit}>
                    <div className='mb-3'>
                        <label htmlFor="fname"><strong> First Name</strong></label>
                        <input type="fname" placeholder='Enter First Name' name = 'firstName'
                        onChange={handleInput} className='form-control rounded-0'/>
                        {errors.firstName && <span className = 'text-danger'> {errors.firstName}</span>}
                    </div>
                    <div className='mb-3'>
                        <label htmlFor="lname"><strong> Last Name</strong></label>
                        <input type="lname" placeholder='Enter Last Name' name = 'lastName'
                        onChange={handleInput} className='form-control rounded-0'/>
                        {errors.lastName && <span className = 'text-danger'> {errors.lastName}</span>}
                    </div>
                    <div className='mb-3'>
                        <label htmlFor="email"><strong>Email</strong></label>
                        <input type="email" placeholder='Enter Email' name = 'email'
                        onChange={handleInput} className='form-control rounded-0'/>
                        {errors.email && <span className = 'text-danger'> {errors.email}</span>}
                    </div>
                    <div className='mb-3'>
                        <label htmlFor="password"><strong>Password</strong></label>
                        <input type="password" placeholder='Enter Password' name = 'password'
                        onChange={handleInput} className='form-control rounded-0'/>
                        {errors.password && <span className = 'text-danger'> {errors.password}</span>}

                    </div>
                    <div>
                        <label htmlFor="gender"><strong>Gender</strong></label>
                        <select id="gender" value={gender} onChange={handleChange}>
                        <option value="">Select Gender</option>
                        <option value="M">Male</option>
                        <option value="F">Female</option>
                        </select>
                    </div>
                    <div className='mb-3'>
                        <label htmlFor="hometown"><strong>Hometown</strong></label>
                        <input type="hometown" placeholder='Enter Hometown' name = 'hometown'
                        onChange={handleInput} className='form-control rounded-0'/>
                        {errors.hometown && <span className = 'text-danger'> {errors.hometown}</span>}
                    </div>
                    <div>
                        <label htmlFor="dob"><strong>Date of Birth</strong></label>
                        <input type="date" id="dob" value={dob} onChange={handleDateChange} />
                    </div>
                    <button className='btn btn-success w-100 rounded-0'><strong>Sign up</strong></button>
                    <p></p>
                    <Link to="/" className='btn btn-default border w-100 bg-light rounded-0 text-decoration-none'>Log in</Link>                </form>
            </div>
        </div>
    )
}

export default Signup
